options.css:
:root{
  --accent-color-1: #e79cfe;
  --accent-color-2: #e79cfe;
  --color-1-a     : #63d0df;
  --color-1-b     : #a1c0eb;
  --color-2-a     : #e79cfe;
  --color-2-b     : #ff9cb1;
  --color-3-a     : #ffad00;
  --color-3-b     : #f7e062;
  --color-4-a     : #92cf9c;
  --color-4-b     : #cabab4;
  --color-5-a     : #df6373;
  --color-5-b     : #df63ae;
  --foloder-color : #777777
}

#main a { 
  position     : relative;
  margin-bottom: 30px;
  box-sizing   : border-box;
  min-width    : 100px;
  transition   : all 0.2s ease-in-out !important;
}

li a::before{
  position  : absolute;
  content   : "";
  top       : 0;
  right     : 0;
  bottom    : 0;
  left      : 0;
  background: linear-gradient(to right, var(--accent-color-1), var(--accent-color-2)) !important;
  opacity   : 0;
  z-index   : -1;
  transition: opacity 0.3s linear;
}

a:hover::before{
  opacity: 1;
}

#main li a:hover{
 box-shadow: 0 10px 20px -5px #6b5874 !important;
 transform : scale(1.1) translateY(-5px);
}


#main a .icon{
  filter: drop-shadow(0px 4px 6px rgba(0,0,0,0.5)) opacity(80%);
}

#main li:nth-of-type(5n) a{
  background: linear-gradient(to right, var(--color-1-a), var(--color-1-b));
  box-shadow: 0 5px 20px -5px var(--color-1-a);
}

#main li:nth-of-type(5n+1) a{
  background: linear-gradient(to right, var(--color-2-a), var(--color-2-b));
  box-shadow: 0 5px 20px -5px var(--color-2-a);
}

#main li:nth-of-type(5n+2) a{
  background: linear-gradient(to right, var(--color-3-a), var(--color-3-b));
  box-shadow: 0 5px 20px -5px var(--color-3-a);
}

#main li:nth-of-type(5n+3) a{
  background: linear-gradient(to right, var(--color-4-a), var(--color-4-b));
  box-shadow: 0 5px 20px -5px var(--color-4-a);
}

#main li:nth-of-type(5n+4) a{
  background: linear-gradient(to right, var(--color-5-a), var(--color-5-b));
  box-shadow: 0 5px 20px -5px var(--color-5-a);
}

.folder{
  background: var(--foloder-color) !important;
  box-shadow: 0 5px 20px -5px var(--foloder-color) !important;
}

#main .null a{
  background: var(--foloder-color) !important;
  box-shadow: 0 5px 20px -5px var(--foloder-color) !important;
}

#main .column {
  margin-right: 20px;
}
